import { useState } from 'react';

const CreateAuction = () => {

     const [title, setTitle] = useState('');
     const [details, setBody] = useState('');
     const [reserve_price, setPrice] = useState(0);
     const [enddate, setDate] = useState('');
     const [isPending, setIsPending] = useState(false)

     const submit = (e) => {
           e.preventDefault();
           const auction = { title, details, reserve_price, end_date } 

           setIsPending(true);

           fetch('http://localhost:3000/api/v1/auctions', {
                 method: 'POST', 
                 // mode: 'cors',
                  credentials: 'include',
                  headers: { "Content-Type": 'applications/json' },
                  body: JSON.stringify(auction)
            }).then(() => {
            console.log('new auction added!');
            setIsPending(false)
            })

            return(
            <div className='create'>
                  <h3>Create A New Auction!</h3>
                  <form onSubmit={submit}>
                        <label>Auction title:</label>
                        <input 
                              type='text' 
                              required
                              value={title}
                              onChange={(e) => {setTitle(e.target.value)}}
                        />

                        <label>Description:</label>
                        <textarea 
                              required
                              value={details}
                              onChange={(e) => {setBody(e.target.value)}}
                        />

                        <label>Ends at:</label>
                        <input 
                              type='date'
                              required
                              value={end_date}
                              onChange={(e) => {setDate(e.target.value)}} 
                        />

                        <label>Reserved Price:</label>
                        <input
                              type='number'
                              value={reserve_price}
                              onChange={(e) => {setPrice(e.target.value)}}
                        />

                        { !isPending && <button>Create</button> }
                        { isPending && <button disabled>Adding auction...</button> }
                  </form>
            </div>
            );
      };
};

export default CreateAuction;
