import { useState } from 'react';

const CreateAuction = () => {

     const [title, setTitle] = useState('');
     const [body, setBody] = useState('');
     const [price, setPrice] = useState(0);
     const [date, setDate] = useState('');
     const [isPending, setIsPending] = useState(false)

     const submit = (e) => {
         e.preventDefault();
        const auction = { title, body, price, date } 

        setIsPending(true);

        fetch('http://localhost:5000/api/v1/auctions', {
            method: 'POST', 
            // mode: 'cors',
            credentials: 'include',
            headers: { "Content-Type": 'applications/json' },
            body: JSON.stringify(auction)
        }).then(() => {
            console.log('new blog added!');
            setIsPending(false)
        })

    return(
      <div className='create'>
            <h3>Create A New Auction!</h3>
            <form onSubmit={submit}>
                  <label>Auction title:</label>
                  <input 
                        type='text' 
                  required
                value={title}
                onChange={(e) => {setTitle(e.target.value)}}/>

                <label>Description:</label>
                <textarea 
                required
                value={body}
                onChange={(e) => {setBody(e.target.value)}}/>

                <label>Ends at:</label>
                <input 
                type='date'
                required
                value={date}
                onChange={(e) => {setDate(e.target.value)}} />

                <label>Reserved Price:</label>
                <input type='number'
                value={price}
                onChange={(e) => {setPrice(e.target.value)}}/>

                { !isPending && <button>Create</button> }
                { isPending && <button disabled>Adding auction...</button> }
            </form>
      </div>
      );
}
}

export default CreateAuction;
